protected string ObtenerXML(DevExpress.Web.ASPxGridView gv, string columna)
        {
            if (gv.Selection.Count > 0)
            {
                List<object> ids = gv.GetSelectedFieldValues(columna);
                DataTable tabla = new DataTable("Empresa");
                tabla.Columns.Add("id");

                foreach (object id in ids)
                {
                    DataRow dr = tabla.NewRow();
                    dr["id"] = id.ToInt();
                    tabla.Rows.Add(dr);
                }

                DataSet dsDatos = new DataSet();
                dsDatos.Tables.Add(tabla);

                return dsDatos.GetXml();
            }
            else
            {
                return "<Newdataset></Newdataset>";
            }
        }

